  }

  public function getFieldsEdit()
  {
    return array(
      'id' => array(),
      'name' => array(),
      'country' => array(),
      'image' => array(),
      'created_at' => array(),
      'updated_at' => array(),
    );
  }

  public function getFieldsNew()
  {
    return array(
      'id' => array(),
      'name' => array(),
      'country' => array(),
      'image' => array(),
      'created_at' => array(),
      'updated_at' => array(),
    );
